import { useState } from 'react'
const axios = require('axios')

const Index = () => {

  const [beerRes, setBeerRes] = useState([])

  const getBeers = (e) => {
    e.preventDefault()

    axios.get('https://api.punkapi.com/v2/beers')
      .then(res => setBeerRes(res.data))
      .catch(err => console.log(err))
  }

  return (
    <div>
      <h4>Beer Punk</h4>
      <button onClick={getBeers}>Get All Beers</button>
      {
        beerRes.length >= 1 ? beerRes.map((beer, idx) => {
          return <p>{beer.name}</p>
        })
          : ''
      }
    </div>
  )
}

export default Index
