import { useState } from 'react'
const axios = require('axios')

const Index = () => {

  const [beerRes, setBeerRes] = useState([])

  const getBeers = (e) => {
    e.preventDefault()

    axios.get('https://api.punkapi.com/v2/beers')
      .then(res => {
        setBeerRes(res.data)
        console.log(res.data)
      })
      .catch(err => console.log(err))
  }

  return (
    <div>
      <h4>Beer Punk</h4>
      <button onClick={getBeers}>Get All Beers</button>
      {
        beerRes.length >= 1 ?
          <div>
            <select>
              <option value="">--ABV Range--</option>
              <option value="">Fart</option>
            </select>
            {
              beerRes.map((beer, idx) => {
                return <p key={idx}>{beer.abv}</p>
              })
            }
          </div>
          : ''
      }
    </div>
  )
}

export default Index
